DROP TABLE if EXISTS blog;CREATE TABLE blog (  id INTEGER PRIMARY KEY AUTOINCREMENT,  title VARCHAR(30) NOT NULL,  text TEXT NOT NULL ,  type INTEGER NOT NULL DEFAULT 0,  time DATE NOT NULL);DROP TABLE if EXISTS tag;CREATE TABLE tag (  id INTEGER PRIMARY KEY AUTOINCREMENT,  type CHAR(10) NOT NULL);DROP TABLE if EXISTS blog_tag;CREATE TABLE blog_tag(  blog_id INT NOT NULL ,  tag_id INT NOT NULL ,  CONSTRAINT blog_id_fk FOREIGN KEY (blog_id) REFERENCES blog(id),  CONSTRAINT tag_id_fk FOREIGN KEY (tag_id) REFERENCES tag(id));DROP TABLE if EXISTS subject;CREATE TABLE subject (  id INTEGER PRIMARY KEY AUTOINCREMENT,  title VARCHAR(30) NOT NULL ,  describe TEXT NOT NULL ,  address VARCHAR(120) NOT NULL ,  time DATE NOT NULL ,  img VARCHAR(255) NOT NULL);DROP TABLE if EXISTS subject_tag;CREATE TABLE subject_tag (  subject_id INT NOT NULL ,  tag_id INT NOT NULL ,  CONSTRAINT subject_id_fk FOREIGN KEY (subject_id) REFERENCES subject(id),  CONSTRAINT tag_id_fk FOREIGN KEY (tag_id) REFERENCES tag(id));