DROP TABLE if EXISTS blog;CREATE TABLE blog (  id INTEGER PRIMARY KEY AUTOINCREMENT,  title VARCHAR(30) NOT NULL,  content BLOB NOT NULL ,  summary BLOB NOT NULL,  time DATE NOT NULL);DROP TABLE if EXISTS article;CREATE TABLE article (    id INTEGER PRIMARY KEY AUTOINCREMENT,    title VARCHAR(30) NOT NULL,    content BLOB NOT NULL,    time DATE NOT NULL);DROP TABLE if EXISTS tag;CREATE TABLE tag (  id INTEGER PRIMARY KEY AUTOINCREMENT,  type CHAR(15) NOT NULL);DROP TABLE if EXISTS tips;CREATE TABLE tips(    id INTEGER PRIMARY KEY AUTOINCREMENT,    title VARCHAR(30) NOT NULL,    content BLOB,    time DATE NOT NULL);DROP TABLE if EXISTS blog_tag;CREATE TABLE blog_tag(  blog_id INT NOT NULL ,  tag_id INT NOT NULL ,  PRIMARY KEY(blog_id, tag_id),  CONSTRAINT blog_id_fk FOREIGN KEY (blog_id) REFERENCES blog(id),  CONSTRAINT tag_id_fk FOREIGN KEY (tag_id) REFERENCES tag(id));DROP TABLE if EXISTS article_tag;CREATE TABLE article_tag(    article_id INT NOT NULL,    tag_id INT NOT NULL,    PRIMARY KEY(article_id, tag_id),    CONSTRAINT article_id_fk FOREIGN KEY (article_id) REFERENCES article(id),    CONSTRAINT tag_id_fk FOREIGN KEY (tag_id) REFERENCES tag(id));DROP TABLE if EXISTS tips_tag;CREATE TABLE tips_tag(    tip_id INT NOT NULL,    tag_id INT NOT NULL,    PRIMARY KEY(tip_id, tag_id),    CONSTRAINT tip_id_fk FOREIGN KEY (tip_id) REFERENCES tips(id),    CONSTRAINT tag_id_fk FOREIGN KEY (tag_id) REFERENCES tag(id));DROP TABLE if EXISTS about;CREATE TABLE about(    content BLOB NOT NULL);INSERT INTO about VALUES('##talk about yourself');DROP TABLE if EXISTS banner;CREATE TABLE banner(    id INTEGER PRIMARY KEY AUTOINCREMENT,    content VARCHAR(256) NOT NULL);INSERT INTO banner VALUES(null, '你现在遭遇的不幸，是你荒废时间的报应。你现在在干什么，而你该干什么？');DROP TABLE if EXISTS account;CREATE TABLE account (  id INTEGER PRIMARY KEY AUTOINCREMENT,  username VARCHAR(30) NOT NULL,  password VARCHAR(32) NOT NULL,  type INT NOT NULL);INSERT INTO account VALUES(null, "admin", 123456, 0);